// Convert payload to boolean
const state = msg.payload === 1 || msg.payload === '1' || msg.payload === true;
flow.set('masterOn', state);

// Send to UI to update button
msg.payload = { masterOn: state };
return msg;

const TX_STATUS = {
    0: { text: 'TX_OFF', color: '#888' },
    1: { text: 'TX_LOCALIZATION', color: '#FFA500' },
    2: { text: 'TX_DEPLOY', color: '#4CAF50' },
    3: { text: 'FULLY_CHARGED', color: '#4A90E2' },
    4: { text: 'TX_ALERT', color: '#FF3D3D' }
};

const RX_STATUS = {
    0: { text: 'RX_NOT_PRESENT', color: '#888' },
  1: { text: 'RX_CONNECTED', color: '#888' },
    2: { text: 'RX_CHARGING', color: '#4CAF50' },
    3: { text: 'RX_MISALIGNED', color: '#FFA500' },
    4: { text: 'RX_FULLY_CHARGED', color: '#4A90E2' },
    5: { text: 'RX_ALERT', color: '#FF3D3D' }
};

const unitId = msg.payload.unit_id || msg.topic.split('/')[1];
let units = flow.get('units') || {};

if (!units[unitId]) {
    units[unitId] = {
        unitId: unitId,
        lastSeen: Date.now(),
        alerts: { tx: {}, rx: {} }
    };
}

units[unitId].lastSeen = Date.now();

if (msg.payload.tx) {
    const tx = msg.payload.tx;
    const statusInfo = TX_STATUS[tx.status] || { text: 'UNKNOWN', color: '#888' };

    units[unitId].tx = {
        mac: tx.mac || 'Unknown',
        id: tx.id || 0,
        voltage: tx.voltage || 0,
        current: tx.current || 0,
        temp: Math.max(tx.temp1 || 0, tx.temp2 || 0),
        status: statusInfo.text,
        statusColor: statusInfo.color,
        statusCode: tx.status,
        power: (tx.voltage || 0) * (tx.current || 0)
    };
}

if (msg.payload.rx) {
    const rx = msg.payload.rx;
    const statusInfo = RX_STATUS[rx.status] || { text: 'UNKNOWN', color: '#888' };

    units[unitId].rx = {
        mac: rx.mac || 'Unknown',
        id: rx.id || 0,
        voltage: rx.voltage || 0,
        current: rx.current || 0,
        temp: Math.max(rx.temp1 || 0, rx.temp2 || 0),
        status: statusInfo.text,
        statusColor: statusInfo.color,
        statusCode: rx.status,
        power: (rx.voltage || 0) * (rx.current || 0)
    };
}

if (units[unitId].tx && units[unitId].rx && units[unitId].tx.power > 0) {
    units[unitId].efficiency = ((units[unitId].rx.power / units[unitId].tx.power) * 100).toFixed(1);
} else {
    units[unitId].efficiency = 0;
}

flow.set('units', units);
return { payload: units };

const unitId = msg.topic.split('/')[1];
let units = flow.get('units') || {};

if (!units[unitId]) {
    units[unitId] = {
        unitId: unitId,
        lastSeen: Date.now(),
        tx: {},
        rx: {}
    };
}

units[unitId].lastSeen = Date.now();
units[unitId].alerts = {
    tx: msg.payload.tx || {},
    rx: msg.payload.rx || {}
};

flow.set('units', units);
return { payload: units };

let units = flow.get('units') || {};
const now = Date.now();
const TIMEOUT = 2 * 60 * 1000;

const active = [];
const toDelete = [];

for (let id in units) {
    if (now - units[id].lastSeen < TIMEOUT) {
        active.push(units[id]);
    } else {
        toDelete.push(id);
    }
}

for (let id of toDelete) {
    delete units[id];
}

flow.set('units', units);
active.sort((a, b) => parseInt(a.unitId) - parseInt(b.unitId));

const masterOn = flow.get('masterOn') || false;

msg.payload = {
    units: active,
    masterOn: masterOn,
    timestamp: new Date().toISOString()
};

return msg;
